    <div class="mt-4">
      <b-card class="mb-3">
        <b-card-text>
          <div class="header_div">
            <h3>
              <i class="far fa-address-card"></i>
              {{artista.nombre}} {{ artista.apellido }}
            </h3>
            <h3>{{ artista.tipo }}</h3>
          </div>

          <hr />
          <h5>{{ artista.descripcion }}</h5>
